const ConsumerHomePage = () => {
  const [measurements, setMeasurements] = useState({
    bust: 90,
    waist: 70,
    hips: 100
  });

  const [brands, setBrands] = useState([]);
  const [garmentTypes, setGarmentTypes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Handle measurement changes
  const handleMeasurementChange = (type, value) => {
    setMeasurements(prev => ({
      ...prev,
      [type]: value
    }));
  };

  // Fetch brands and garment types from the database
  useEffect(() => {
    const fetchDropdownData = async () => {
      try {
        setLoading(true);

        // Fetch brands
        const brandsResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/brands`);
        if (brandsResponse.data && brandsResponse.data.brands) {
          setBrands(brandsResponse.data.brands);
        }

        // Fetch garment types
        const garmentTypesResponse = await axios.get(`${process.env.REACT_APP_API_URL}/api/admin/garment-types`);
        if (garmentTypesResponse.data && garmentTypesResponse.data.garmentTypes) {
          setGarmentTypes(garmentTypesResponse.data.garmentTypes);
        }

        setError(null);
      } catch (err) {
        console.error('Error fetching dropdown data:', err);
        setError('Failed to load dropdown options. Using default values.');
      } finally {
        setLoading(false);
      }
    };

    fetchDropdownData();
  }, []);

  return (
    <>
      <SizibleNavigation />
      <PageContainer>
        <SizibleCard elevated style={{ maxWidth: '800px', margin: '0 auto' }}>
          <h1>Find Your Perfect Size</h1>
          <p>Enter your measurements and preferences to get personalized size recommendations.</p>

          <form>
            <FormSection>
              <SectionTitle>1. Select Your Preferences</SectionTitle>

              <div>
                <label htmlFor="retailer">Select Retailer</label>
                <select id="retailer">
                  <option value="">-- Select a retailer --</option>
                  <option value="1">Marks & Spencer</option>
                  <option value="2">Next</option>
                  <option value="3">Zara</option>
                </select>
              </div>

              <div>
                <label htmlFor="brand">Select Brand</label>
                <select id="brand" disabled={loading || brands.length === 0}>
                  {loading ? (
                    <option value="">Loading brands...</option>
                  ) : error ? (
                    <option value="">-- Select a brand --</option>
                  ) : brands.length === 0 ? (
                    <option value="">No brands available</option>
                  ) : (
                    <>
                      <option value="">-- Select a brand --</option>
                      {brands.map(brand => (
                        <option key={brand.id} value={brand.id}>{brand.name}</option>
                      ))}
                    </>
                  )}
                </select>
              </div>

              <div>
                <label htmlFor="garmentType">Garment Type</label>
                <select id="garmentType" disabled={loading || garmentTypes.length === 0}>
                  {loading ? (
                    <option value="">Loading garment types...</option>
                  ) : error ? (
                    <option value="">-- Select garment type --</option>
                  ) : garmentTypes.length === 0 ? (
                    <option value="">No garment types available</option>
                  ) : (
                    <>
                      <option value="">-- Select garment type --</option>
                      {garmentTypes.map(type => (
                        <option key={type.id} value={type.id}>{type.name}</option>
                      ))}
                    </>
                  )}
                </select>
              </div>
            </FormSection>

            <FormDivider />

            <FormSection>
              <SectionTitle>2. Enter Your Measurements</SectionTitle>
              <p>Drag the sliders or enter your measurements directly.</p>

              <MeasurementInput 
                label="Bust" 
                value={measurements.bust} 
                onChange={(value) => handleMeasurementChange('bust', value)} 
                min={60}
                max={150}
              />

              <MeasurementInput 
                label="Waist" 
                value={measurements.waist} 
                onChange={(value) => handleMeasurementChange('waist', value)} 
                min={50}
                max={140}
              />

              <MeasurementInput 
                label="Hips" 
                value={measurements.hips} 
                onChange={(value) => handleMeasurementChange('hips', value)} 
                min={70}
                max={160}
              />
            </FormSection>

            <SizibleButton type="submit" fullWidth style={{ marginTop: '20px' }}>
              Get Recommendations
            </SizibleButton>
          </form>
        </SizibleCard>
      </PageContainer>
    </>
  );
};

