/**
 * Main App Component
 * 
 * This is the root component of the Sizible Fashion Style Advice application.
 * It sets up routing and global providers.
 */

import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import theme from './styles/theme';
import GlobalStyles from './styles/GlobalStyles';

// Placeholder pages - these will be implemented later
const HomePage = () => <div className="container mt-4"><h1>Sizible Fashion Style Advice</h1><p>Welcome to the Sizible platform</p></div>;
const RetailerLoginPage = () => <div className="container mt-4"><h1>Retailer Login</h1></div>;
const RetailerDashboardPage = () => <div className="container mt-4"><h1>Retailer Dashboard</h1></div>;
const ConsumerHomePage = () => <div className="container mt-4"><h1>Consumer Home</h1></div>;
const NotFoundPage = () => <div className="container mt-4"><h1>404 - Page Not Found</h1></div>;

/**
 * App component - the main application component
 * 
 * @returns {JSX.Element} The App component
 */
function App() {
  return (
    <ThemeProvider theme={theme}>
      <GlobalStyles />
      <Router>
        <Routes>
          {/* Public routes */}
          <Route path="/" element={<HomePage />} />

          {/* Retailer routes */}
          <Route path="/retailer/login" element={<RetailerLoginPage />} />
          <Route path="/retailer/dashboard" element={<RetailerDashboardPage />} />

          {/* Consumer routes */}
          <Route path="/consumer" element={<ConsumerHomePage />} />

          {/* Fallback routes */}
          <Route path="/404" element={<NotFoundPage />} />
          <Route path="*" element={<Navigate to="/404" replace />} />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
